import React, { useEffect, useState } from 'react';
// think of useState as giving your component a memory which persists bw re-renders
import { themeContext, themes } from "./themeContext";
import { useContext } from 'react';

// Wrapper component to provide context
export function ToggleTheme() {
    const [currentTheme, setCurrentTheme] = useState(themes.light);

    const toggleTheme = () => {
      setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

    return (
      <themeContext.Provider value={currentTheme}>
        <ClickCounter/>
        <button onClick={toggleTheme}> Toggle Theme </button>
      </themeContext.Provider>
    );
}

   //export default ToggleTheme;

export function ClickCounter() {
  const [count, setCount] = useState(0);
  // ^This line does two things:
  // It creates a state variable (state) with an initial value.
  // It provides a function (setState) to update that state

  const handleClick = () => {
    setCount(count + 1);
  };

  // updates document title (title u see in the tab)
  useEffect(() => {
    document.title = `You clicked ${count} times`;
  }, [count]);

  const theme = useContext(themeContext);
    return (
   <div
     style={{
       background: theme.background,
       color: theme.foreground,
       padding: "20px",
     }}
   >
     <p>You clicked {count} times </p>
     <button
       onClick={() => setCount(count + 1)}
       style={{ background: theme.foreground, color: theme.background }}
     >
       Click me
     </button>
   </div>
 );
}

export function LikeButton() {
    const [currentlike, setLike] = useState(false);

    const likeButton = () => {
        setLike(!currentlike);
    };

    return (
        <button
            onClick={likeButton}
            className={`likebtn ${currentlike ? 'currentlike' : ''}`}
        >
            {currentlike ? '❤️' : '♡'}
        </button>
    );
}
