import React, { useEffect, useState } from 'react'; // think of useState as giving your component a memory which persists bw re-renders
import { themeContext, themes } from "./themeContext";
import { useContext } from 'react';

export function ToggleTheme() { // Wrapper component to provide context
    const [currentTheme, setCurrentTheme] = useState(themes.light);

    const toggleTheme = () => {
      setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

    return (
      <themeContext.Provider value={currentTheme}>
        <ClickCounter/>
        <button onClick={toggleTheme}> Toggle Theme </button>
      </themeContext.Provider>
    );
}

export function ClickCounter() {
  const [count, setCount] = useState(0);

  const handleClick = () => {
    setCount(count + 1);
  };

  useEffect(() => { 
    document.title = `You clicked ${count} times`;
  }, [count]);

  const theme = useContext(themeContext);
    return (
   <div
     style={{
       background: theme.background,
       color: theme.foreground,
       padding: "20px",
     }}
   >
     <p>You clicked {count} times </p>
     <button
       onClick={() => setCount(count + 1)}
       style={{ background: theme.foreground, color: theme.background }}
     >
       Click me
     </button>
   </div>
 );
}

export function LikeButton() {
    const [currentlike, setLike] = useState(false);

    const likeButton = () => {
        setLike(!currentlike);
    };

    return (
        <button
            onClick={likeButton}
            className={`likebtn ${currentlike ? 'currentlike' : ''}`}
        >
            {currentlike ? '❤️' : '♡'}
        </button>
    );
}

export function DummyComponent(){
    const [fullList, setFullList] = useState(['item1', 'item2', 'item3', 'item4'])
    const [favList, setFavList] = useState([]) 

    const handleFavAddClick=(e)=>{
        setFavList(preState=>[...preState, e])
        setFullList(preState=> preState.filter(item => item !== e))
    }


    return(
        <div>
            Full List (add to fav by clicking them)
            <ul>
             {
                 fullList.map(e=> <li key={e} onClick={()=>handleFavAddClick(e)}>{e}</li>)
             }
            </ul>

            Fav List
            <ul>
             {
                 favList.map(e=> <li key={e}>{e}</li>)
             }
            </ul>
        </div>
    )
  }
