import './App.css';
import { Label, Note } from "./types";
import { dummyNotesList } from "./constants";

//import ToggleTheme from "./hooksExercise";
import React, { useEffect, useState } from 'react'; //useeffect not seen anywhere
//import ToggleTheme from './hooksExercise';
import { ThemeContext, themes } from "./themeContext";
import { ClickCounter } from './hooksExercise';

function App() {
  const [notes, setNotes] = useState(dummyNotesList);

  const initialNote = {
    id: -1,
    title: "",
    content: "",
    label: Label.other,
  };

  const [createNote, setCreateNote] = useState(initialNote);

  const createNoteHandler = (event: React.FormEvent) => {
    event.preventDefault();
    console.log("title: ", createNote.title);
    console.log("content: ", createNote.content);
    createNote.id = notes.length + 1;
    setNotes([createNote, ...notes]);
    setCreateNote(initialNote);
  };

  /*
  const deleteNote = (thingy) => {
    let remainingNotes = notes.filter(
      (some) => some.id !== thingy.id
    );
    setNotes(remainingNotes);
  }
  */

  function ToggleTheme() {
    const [currentTheme, setCurrentTheme] = useState(themes.light);

    const toggleTheme = () => {
      setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

    return (
      <ThemeContext.Provider value={currentTheme}>
        <button onClick={toggleTheme}>Toggle Theme</button>
        <ClickCounter/>
        </ThemeContext.Provider>
    );
  }

  return (
    <div className='app-container'>
      <form className="note-form" onSubmit={createNoteHandler}>
        <div>
          <input
                placeholder="Note Title"
                onChange={(event) => setCreateNote({...createNote, title: event.target.value })} required>
          </input>
        </div>

        <div>
          <textarea
            onChange={(event) => setCreateNote({...createNote, content: event.target.value })} required>
          </textarea>
        </div>

        <div>
          <select
            onChange={(event) => setCreateNote({...createNote, label: event.target.value as Label })} required>
            <option value={Label.personal}>Personal</option>
            <option value={Label.study}>Study</option>
            <option value={Label.work}>Work</option>
            <option value={Label.other}>Other</option>
          </select>
        </div>

        <div><button type="submit">Create Note</button></div>
      </form>

      <div className="notes-grid">
        {notes.map((note) => (
          <div key={note.id} className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
            <p>{note.label}</p>
          </div>
        ))}
      </div>

      <ToggleTheme/>

    </div>
  );
}

export default App;
