import './App.css';
import { Label, Note } from "./types";
import { dummyNotesList } from "./constants";
import ToggleTheme from "./hooksExercise";

import { useContext } from 'react';
import React, { useEffect, useState } from 'react';
import { ThemeContext, themes } from "./themeContext";

function App() {
  function ClickCounter() { // only referencing no need to export
    const [count, setCount] = useState(0);

    useEffect(() => {
        document.title = `You clicked ${count} times`;
    }, [count]);

    const theme = useContext(ThemeContext);

    return (
        <div style={{background: theme.background, color: theme.foreground, padding: "20px",}}>
            <p>You clicked {count} times</p>
            <button onClick={() => setCount(count + 1)} style={{background: theme.foreground, color: theme.background}}>Click me!</button>
        </div>
    );
}

    const [currentTheme, setCurrentTheme] = useState(themes.light);

    const toggleTheme = () => {
        setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    };

  return (
    <ThemeContext.Provider value={currentTheme}>
    <div className='app-container'>
      <form className="note-form">
        <div><input placeholder="Note Title"></input></div>
        <div><textarea></textarea></div>
        <div><button type="submit">Create Note</button></div>
      </form>

      <div className="notes-grid">
        {dummyNotesList.map((note) => (
          <div key={note.id} className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
            <p>{note.label}</p>
          </div>
        ))}
      </div>

      <ToggleTheme/>
    </div>
    </ThemeContext.Provider>
  );
}

export default App;
