import './App.css';
import { Label, Note } from "./types";
import { dummyNotesList } from "./constants";
import ToggleTheme from "./hooksExercise";

import { useContext } from 'react';
import React, { useEffect, useState } from 'react';
import { ThemeContext, themes } from "./themeContext";

function App() {
  const theme = useContext(ThemeContext);
  const [currentTheme, setCurrentTheme] = useState(themes.light);
  const toggleTheme = () => {
    setCurrentTheme(currentTheme === themes.light ? themes.dark : themes.light);
    console.log(currentTheme);
  };

  const [count, setCount] = useState(0);

  useEffect(() => {
    document.title = `current theme is ${currentTheme}`; // it's taking place but no visible changes on the app
    console.log(currentTheme);
    setCount(count + 1);
  }, [currentTheme]);

  return (
    <ThemeContext.Provider value={currentTheme}>
    <div className='app-container' style={{background: theme.background, color: theme.foreground, padding: "20px",}}>
      <form className="note-form">
        <div><input placeholder="Note Title"></input></div>
        <div><textarea></textarea></div>
        <div><button type="submit">Create Note</button></div>
      </form>

      <div className="notes-grid">
        {dummyNotesList.map((note) => (
          <div key={note.id} className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
            <p>{note.label}</p>
          </div>
        ))}
      </div>

      <button onClick={toggleTheme}>test</button>
    </div>
    </ThemeContext.Provider>
  );
}

export default App;
