import './App.css';
import { Label, Note } from "./types"; // Import the Label type from the appropriate module
import { dummyNotesList } from "./constants"; // Import the dummyNotesList from the appropriate module
import { ClickCounter } from "./hooksExercise";
import { ToggleTheme } from "./hooksExercise";
//import { LikeButton } from "./hooksExercise";
import './darkMode.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    if (theme === 'light') {
      setTheme('dark');
    }
    else {
      setTheme('light');
    }
  };

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);



  function LikeButton() {
    const [currentlike, setLike] = useState(false);

    const likeButton = () => {
        setLike(!currentlike);
    };

    return (
        <button onClick={likeButton}>
            {currentlike ? '❤️' : '♡'}
        </button>
    );
  }

  return (
    <div className='app-container ${theme}'>
      <form className="note-form ${theme}">
        <div><input placeholder="Note Title"></input></div>

        <div><textarea></textarea></div>

        <div><button type="submit">Create Note</button></div>
      </form>
    <div className="notes-grid ${theme}">
      {dummyNotesList.map((note) => (
        <div key={note.id} className="note-item ${theme}">
          <div className="notes-header">
            <LikeButton/>
            <button>x</button>
          </div>
          <h2 style={{color:'black'}}> {note.title} </h2>
          <p style={{color:'black'}}> {note.content} </p>
          <p style={{color:'black'}}> {note.label} </p>
        </div>
      ))}
    </div>
    <div className="favorites">
      <button onClick={toggleTheme}>Toggle Theme</button>
      <h2>List of favorites:</h2>
      <p>hiiiiii testing</p>
      <p>testing some more heyyyyyyy</p>
    </div>
    </div>
 );
}

export default App;
